import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const ExpenseForm = () => {
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const [expense, setExpense] = useState({
    description: '',
    amount: '',
    category: '',
    subcategory: '',
    paymentMethod: '',
    tags: []
  });
  const [error, setError] = useState('');

  // Predefined lists for dropdowns
  const categories = [
    'Food', 'Transportation', 'Entertainment', 
    'Utilities', 'Housing', 'Healthcare', 
    'Education', 'Personal', 'Shopping', 
    'Miscellaneous'
  ];

  const subcategories = {
    'Food': ['Groceries', 'Restaurants', 'Cafes', 'Takeout'],
    'Transportation': ['Public Transit', 'Fuel', 'Taxi/Ride-share', 'Maintenance'],
    'Housing': ['Rent', 'Mortgage', 'Repairs', 'Maintenance'],
    'Utilities': ['Electricity', 'Water', 'Internet', 'Phone'],
    'Healthcare': ['Medicines', 'Doctor Visits', 'Insurance', 'Wellness'],
    'Entertainment': ['Movies', 'Concerts', 'Streaming', 'Hobbies'],
    'Shopping': ['Clothing', 'Electronics', 'Gifts', 'Personal Care'],
    'Miscellaneous': ['Unexpected', 'Donations', 'Education', 'Other']
  };

  const paymentMethods = [
    'Cash', 'Debit Card', 'Credit Card', 
    'Bank Transfer', 'Digital Wallet', 'Other'
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExpense(prevExpense => ({
      ...prevExpense,
      [name]: value
    }));
  };

  const handleTagChange = (e) => {
    const tags = e.target.value.split(',').map(tag => tag.trim()).filter(tag => tag);
    setExpense(prevExpense => ({
      ...prevExpense,
      tags
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Validate input
    if (!expense.description || !expense.amount || !expense.category || !expense.paymentMethod) {
      setError('Please fill in all required fields');
      return;
    }

    const payload = {
      description: expense.description,
      amount: parseFloat(expense.amount),
      category: expense.category,
      subcategory: expense.subcategory || 'General',
      tags: expense.tags,
      paymentMethod: expense.paymentMethod,
      userId: currentUser.uid  // Add user ID for authentication
    };

    try {
      const token = await currentUser.getIdToken();
      const response = await axios.post('http://localhost:5000/api/expenses/create', payload, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      // Reset form
      setExpense({
        description: '',
        amount: '',
        category: '',
        subcategory: '',
        tags: [],
        paymentMethod: ''
      });

      // Navigate to expense list
      navigate('/expenses');
    } catch (error) {
      console.error('Error adding expense:', error);
      setError(error.response?.data?.message || 'Failed to add expense. Please try again.');
    }
  };

  return (
    <div className="expense-form-container">
      <h2>➕ Add New Expense</h2>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit} className="expense-form">
        <div className="form-group input-icon-wrapper">
          <label>Description *</label>
          <span className="input-icon">📝</span>
          <input
            type="text"
            name="description"
            value={expense.description}
            onChange={handleChange}
            placeholder="Enter expense description"
            required
            minLength={3}
            maxLength={100}
          />
        </div>

        <div className="form-group input-icon-wrapper">
          <label>Amount (₹) *</label>
          <span className="input-icon">💰</span>
          <input
            type="number"
            name="amount"
            value={expense.amount}
            onChange={handleChange}
            placeholder="Enter expense amount"
            min="0"
            step="0.01"
            required
          />
        </div>

        <div className="form-group">
          <label>Category *</label>
          <select
            name="category"
            value={expense.category}
            onChange={handleChange}
            required
          >
            <option value="">Select Category</option>
            {categories.map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>

        {expense.category && subcategories[expense.category] && (
          <div className="form-group">
            <label>Subcategory</label>
            <select
              name="subcategory"
              value={expense.subcategory}
              onChange={handleChange}
            >
              <option value="">Select Subcategory</option>
              {subcategories[expense.category].map(subcategory => (
                <option key={subcategory} value={subcategory}>{subcategory}</option>
              ))}
            </select>
          </div>
        )}

        <div className="form-group">
          <label>Payment Method *</label>
          <select
            name="paymentMethod"
            value={expense.paymentMethod}
            onChange={handleChange}
            required
          >
            <option value="">Select Payment Method</option>
            {paymentMethods.map(method => (
              <option key={method} value={method}>{method}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Tags (comma-separated)</label>
          <input
            type="text"
            name="tags"
            value={expense.tags.join(', ')}
            onChange={handleTagChange}
            placeholder="Enter tags (e.g., groceries, weekly)"
          />
        </div>

        <button type="submit" className="btn btn-primary">Add Expense</button>
      </form>
    </div>
  );
};

export default ExpenseForm;
