import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, NavLink } from 'react-router-dom';
import ExpenseList from './components/ExpenseList';
import ExpenseForm from './components/ExpenseForm';
import Dashboard from './components/Dashboard';
import './App.css';

function App() {
  const [isScrolled, setIsScrolled] = useState(false);
  const [activeLink, setActiveLink] = useState('');

  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      setIsScrolled(offset > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const navLinks = [
    { to: '/', label: 'Dashboard', icon: 'ðŸ“Š' },
    { to: '/expenses', label: 'Expenses', icon: 'ðŸ’¸' },
    { to: '/add-expense', label: 'Add Expense', icon: 'âž•' }
  ];

  return (
    <Router>
      <div className="App">
        {/* Dynamic Background Particles */}
        <div className="background-particle"></div>
        <div className="background-particle"></div>
        <div className="background-particle"></div>

        {/* Enhanced Navigation Bar */}
        <nav className={`navbar ${isScrolled ? 'navbar-scrolled' : ''}`}>
          <div className="navbar-content">
            <div className="navbar-logo glow-effect">
              ðŸ’° AI Expense Tracker
            </div>
            <div className="navbar-links">
              {navLinks.map((link) => (
                <NavLink 
                  key={link.to}
                  to={link.to} 
                  className={({ isActive }) => 
                    `nav-link ${isActive ? 'active' : ''} glow-effect`
                  }
                  onClick={() => setActiveLink(link.to)}
                >
                  {link.icon} {link.label}
                </NavLink>
              ))}
            </div>
          </div>
        </nav>

        {/* Main Content Area */}
        <div className="main-content floating-element">
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/expenses" element={<ExpenseList />} />
            <Route path="/add-expense" element={<ExpenseForm />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
