import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import { formatCurrency } from '../utils/formatCurrency';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

// Register ChartJS components
ChartJS.register(ArcElement, Tooltip, Legend);

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState({
    totalExpenses: 0,
    monthlyExpenses: 0,
    categoryBreakdown: {},
    recentExpenses: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [monthlyReport, setMonthlyReport] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:5000/api/dashboard');

        console.log('Dashboard Data:', response.data);

        setDashboardData(response.data);
        setError(null);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setError(err.response?.data?.message || 'Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    const fetchMonthlyReport = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/monthly-report');
        setMonthlyReport(response.data);
      } catch (error) {
        console.error('Error fetching monthly report:', error);
      }
    };

    fetchDashboardData();
    fetchMonthlyReport();
  }, []);

  // Prepare Pie Chart Data
  const preparePieChartData = () => {
    const { categoryBreakdown } = dashboardData;

    return {
      labels: Object.keys(categoryBreakdown),
      datasets: [{
        data: Object.values(categoryBreakdown),
        backgroundColor: [
          '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', 
          '#9966FF', '#FF9F40', '#FF6384', '#C9CBCF'
        ]
      }]
    };
  };

  const getInsightIcon = (type) => {
    const icons = {
      saving: 'üí∞',
      warning: '‚ö†Ô∏è',
      trend: 'üìà',
      suggestion: 'üí°',
      achievement: 'üéØ',
      budget: 'üìä',
      spending: 'üí≥',
      income: 'üíµ',
      default: 'üìã'
    };
    return icons[type] || icons.default;
  };

  const renderAIFinancialReport = () => {
    if (!monthlyReport) return null;

    return (
      <div className="ai-insights-section">
        <div className="ai-insights-header">
          <h3>AI Financial Insights</h3>
        </div>

        <div className="ai-insights-card">
          <div className="ai-insights-content">
            {monthlyReport.insights.map((insight, index) => (
              <div 
                key={index} 
                className="ai-tip"
                data-type={insight.type}
                style={{animationDelay: `${index * 0.1}s`}}
              >
                <div className="ai-tip-icon">
                  {getInsightIcon(insight.type)}
                </div>
                <div className="ai-tip-content">
                  <div className="ai-tip-title">
                    {insight.title}
                    {insight.type === 'achievement' && 'üéâ'}
                    {insight.type === 'warning' && '‚ö†Ô∏è'}
                  </div>
                  <div className="ai-tip-description">
                    {insight.description}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {monthlyReport.summary && (
          <div className="ai-insights-card">
            <div className="ai-tip">
              <div className="ai-tip-icon">üìä</div>
              <div className="ai-tip-content">
                <div className="ai-tip-title">Monthly Summary</div>
                <div className="ai-tip-description">
                  {monthlyReport.summary}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };

  // Loading State
  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  // Error State
  if (error) {
    return (
      <div className="error-container">
        <h2>üòï Oops! Something went wrong</h2>
        <p>{error}</p>
        <button onClick={() => window.location.reload()}>
          Retry Loading
        </button>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <h1>Expense Dashboard</h1>

      <div className="dashboard-summary">
        <div className="summary-card">
          <h3>Total Expenses</h3>
          <p>{formatCurrency(dashboardData.totalExpenses)}</p>
        </div>
        <div className="summary-card">
          <h3>Monthly Expenses</h3>
          <p>{formatCurrency(dashboardData.monthlyExpenses)}</p>
        </div>
      </div>

      <div className="dashboard-chart">
        <h2>Expense Distribution</h2>
        {Object.keys(dashboardData.categoryBreakdown).length > 0 ? (
          <Pie 
            data={preparePieChartData()} 
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
                legend: {
                  position: 'bottom'
                }
              }
            }} 
          />
        ) : (
          <p>No expense data available</p>
        )}
      </div>

      {renderAIFinancialReport()}

      <div className="recent-expenses">
        <h2>Recent Expenses</h2>
        {dashboardData.recentExpenses.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Description</th>
                <th>Category</th>
                <th>Amount</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {dashboardData.recentExpenses.map((expense) => (
                <tr key={expense._id}>
                  <td>{expense.description}</td>
                  <td>{expense.category}</td>
                  <td>{formatCurrency(expense.amount)}</td>
                  <td>{new Date(expense.date).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No recent expenses found</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
