import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ExpenseForm = () => {
  const navigate = useNavigate();
  const [expense, setExpense] = useState({
    description: '',
    amount: '',
    category: '',
    subcategory: '',
    tags: [],
    paymentMethod: ''
  });
  const [aiInsights, setAiInsights] = useState(null);
  const [error, setError] = useState(null);

  const categories = [
    'Food', 'Transport', 'Entertainment', 
    'Utilities', 'Housing', 'Healthcare', 
    'Education', 'Personal', 'Shopping', 
    'Miscellaneous'
  ];

  const paymentMethods = [
    'Cash', 'Credit Card', 'Debit Card', 
    'Bank Transfer', 'Other'
  ];

  const subcategories = {
    'Utilities': ['Electricity', 'Water', 'Internet', 'Phone', 'Gas', 'Other'],
    'Shopping': ['Clothing', 'Electronics', 'Home Goods', 'Shoes', 'Accessories', 'Other'],
    'Food': ['Groceries', 'Restaurants', 'Takeout', 'Snacks', 'Other'],
    'Transport': ['Fuel', 'Public Transit', 'Maintenance', 'Parking', 'Other'],
    'Entertainment': ['Movies', 'Concerts', 'Streaming', 'Games', 'Other']
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setAiInsights(null);

    // Validate input
    if (!expense.description || !expense.amount || !expense.category || !expense.paymentMethod) {
      setError('Please fill in all required fields');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/create', {
        description: expense.description,
        amount: parseFloat(expense.amount),
        category: expense.category,
        subcategory: expense.subcategory || 'General',
        tags: expense.tags,
        paymentMethod: expense.paymentMethod
      });

      // Extract AI insights from the response
      if (response.data.aiInsights) {
        setAiInsights(response.data.aiInsights);
      }

      // Reset form and show success message
      setExpense({
        description: '',
        amount: '',
        category: '',
        subcategory: '',
        tags: [],
        paymentMethod: ''
      });

      // Navigate to expense list
      navigate('/');
    } catch (error) {
      console.error('Error adding expense:', error);
      setError(`Failed to add expense: ${error.response?.data?.message || error.message}`);
    }
  };

  return (
    <div className="expense-form-container">
      <h2>Add New Expense</h2>
      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Description</label>
          <input
            type="text"
            placeholder="Expense description"
            value={expense.description}
            onChange={(e) => setExpense({...expense, description: e.target.value})}
            required
          />
        </div>

        <div className="form-group">
          <label>Amount</label>
          <input
            type="number"
            placeholder="Expense amount"
            value={expense.amount}
            onChange={(e) => setExpense({...expense, amount: e.target.value})}
            min="0"
            step="0.01"
            required
          />
        </div>

        <div className="form-group">
          <label>Category</label>
          <select
            value={expense.category}
            onChange={(e) => {
              setExpense({
                ...expense, 
                category: e.target.value,
                subcategory: '' // Reset subcategory when category changes
              });
            }}
            required
          >
            <option value="">Select Category</option>
            {categories.map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>

        {expense.category && subcategories[expense.category] && (
          <div className="form-group">
            <label>Subcategory</label>
            <select
              value={expense.subcategory}
              onChange={(e) => setExpense({...expense, subcategory: e.target.value})}
            >
              <option value="">Select Subcategory</option>
              {subcategories[expense.category].map(subcategory => (
                <option key={subcategory} value={subcategory}>{subcategory}</option>
              ))}
              <option value="Other">Other</option>
            </select>
          </div>
        )}

        <div className="form-group">
          <label>Payment Method</label>
          <select
            value={expense.paymentMethod}
            onChange={(e) => setExpense({...expense, paymentMethod: e.target.value})}
            required
          >
            <option value="">Select Payment Method</option>
            {paymentMethods.map(method => (
              <option key={method} value={method}>{method}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Tags (optional, comma-separated)</label>
          <input
            type="text"
            placeholder="Add tags (e.g. personal, important)"
            value={expense.tags.join(', ')}
            onChange={(e) => setExpense({...expense, tags: e.target.value.split(',').map(tag => tag.trim()).filter(tag => tag)})}
          />
        </div>

        <button type="submit" className="submit-btn">
          Add Expense
        </button>
      </form>

      {aiInsights && (
        <div className="ai-insights">
          <h3>AI Insights</h3>
          <div>
            <strong>Sentiment:</strong> {aiInsights.sentiment}
          </div>
          <div>
            <strong>Recommendation:</strong> {aiInsights.recommendation}
          </div>
        </div>
      )}
    </div>
  );
};

export default ExpenseForm;
