import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import { Link } from 'react-router-dom';

ChartJS.register(ArcElement, Tooltip, Legend);

const ExpenseList = () => {
  const [expenses, setExpenses] = useState([]);
  const [monthlyReport, setMonthlyReport] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    category: '',
    subcategory: '',
    paymentMethod: '',
    minAmount: '',
    maxAmount: ''
  });
  const [appliedFilters, setAppliedFilters] = useState({});

  const categories = [
    'Food', 'Transport', 'Entertainment', 
    'Utilities', 'Housing', 'Healthcare', 
    'Education', 'Personal', 'Shopping', 
    'Miscellaneous'
  ];

  const paymentMethods = [
    'Cash', 'Credit Card', 'Debit Card', 
    'Bank Transfer', 'Other'
  ];

  const subcategories = {
    'Utilities': ['Electricity', 'Water', 'Internet', 'Phone', 'Gas', 'Other'],
    'Shopping': ['Clothing', 'Electronics', 'Home Goods', 'Shoes', 'Accessories', 'Other'],
    'Food': ['Groceries', 'Restaurants', 'Takeout', 'Snacks', 'Other'],
    'Transport': ['Fuel', 'Public Transit', 'Maintenance', 'Parking', 'Other'],
    'Entertainment': ['Movies', 'Concerts', 'Streaming', 'Games', 'Other']
  };

  // Format Indian Rupee
  const formatIndianRupee = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(amount);
  };

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        setLoading(true);
        const expensesResponse = await axios.get('http://localhost:5000/api', { 
          params: appliedFilters 
        });
        const reportResponse = await axios.get('http://localhost:5000/api/monthly-report', { 
          params: appliedFilters 
        });

        setExpenses(expensesResponse.data);
        setMonthlyReport(reportResponse.data);
        setError(null);
      } catch (error) {
        console.error('Error fetching expenses:', error);
        setError(error.message || 'Failed to fetch expenses');
      } finally {
        setLoading(false);
      }
    };

    fetchExpenses();
  }, [appliedFilters]);

  const preparePieChartData = () => {
    const categoryTotals = expenses.reduce((acc, expense) => {
      acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
      return acc;
    }, {});

    return {
      labels: Object.keys(categoryTotals),
      datasets: [{
        data: Object.values(categoryTotals),
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF', '#FF9F40']
      }]
    };
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const applyFilters = () => {
    // Create a new object with only non-empty filter values
    const newAppliedFilters = Object.fromEntries(
      Object.entries(filters)
        .filter(([_, value]) => value !== '')
        .map(([key, value]) => {
          // Ensure numeric filters are parsed correctly
          if (key === 'minAmount' || key === 'maxAmount') {
            return [key, parseFloat(value).toFixed(2)];
          }
          return [key, value];
        })
    );

    // Update applied filters to trigger useEffect
    setAppliedFilters(newAppliedFilters);
  };

  const resetFilters = () => {
    // Reset both filters and applied filters
    setFilters({
      category: '',
      subcategory: '',
      paymentMethod: '',
      minAmount: '',
      maxAmount: ''
    });
    setAppliedFilters({});
  };

  const renderAIFinancialReport = () => {
    if (!monthlyReport) return null;

    return (
      <div className="ai-financial-report">
        <h3>üí° AI Financial Insights</h3>

        <div className="health-assessment">
          <h4>ü©∫ Financial Health Assessment</h4>
          <p>{monthlyReport.healthAssessment}</p>
        </div>

        <div className="spending-categories">
          <h4>üìä Top Spending Categories</h4>
          <ul>
            {monthlyReport.topCategories.map((category, index) => (
              <li key={index}>
                {category.name}: {category.percentage}%
              </li>
            ))}
          </ul>
        </div>

        <div className="budgeting-recommendations">
          <h4>üí∞ Budgeting Recommendations</h4>
          <ul>
            {monthlyReport.recommendations.map((recommendation, index) => (
              <li key={index}>{recommendation}</li>
            ))}
          </ul>
        </div>
      </div>
    );
  };

  if (loading) {
    return <div className="loading">Loading expenses...</div>;
  }

  if (error) {
    return (
      <div className="error-message">
        <h2>Error</h2>
        <p>{error}</p>
        <p>Please ensure the backend server is running on http://localhost:5000</p>
      </div>
    );
  }

  return (
    <div className="expense-list-container">
      <h2>üí∏ Expense List</h2>

      <div className="expense-filters">
        <div className="filter-group">
          <label>Category</label>
          <select 
            name="category" 
            value={filters.category} 
            onChange={handleFilterChange}
          >
            <option value="">All Categories</option>
            {categories.map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>

        {filters.category && subcategories[filters.category] && (
          <div className="filter-group">
            <label>Subcategory</label>
            <select 
              name="subcategory" 
              value={filters.subcategory} 
              onChange={handleFilterChange}
            >
              <option value="">All Subcategories</option>
              {subcategories[filters.category].map(subcategory => (
                <option key={subcategory} value={subcategory}>{subcategory}</option>
              ))}
            </select>
          </div>
        )}

        <div className="filter-group">
          <label>Payment Method</label>
          <select 
            name="paymentMethod" 
            value={filters.paymentMethod} 
            onChange={handleFilterChange}
          >
            <option value="">All Payment Methods</option>
            {paymentMethods.map(method => (
              <option key={method} value={method}>{method}</option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Min Amount (‚Çπ)</label>
          <input 
            type="number" 
            name="minAmount" 
            value={filters.minAmount} 
            onChange={handleFilterChange} 
            placeholder="Minimum Amount"
            min="0"
          />
        </div>

        <div className="filter-group">
          <label>Max Amount (‚Çπ)</label>
          <input 
            type="number" 
            name="maxAmount" 
            value={filters.maxAmount} 
            onChange={handleFilterChange} 
            placeholder="Maximum Amount"
            min="0"
          />
        </div>

        <div className="filter-actions">
          <button 
            className="filter-button apply-filter" 
            onClick={applyFilters}
          >
            üîç Apply Filters
          </button>
          <button 
            className="filter-button reset-filter" 
            onClick={resetFilters}
          >
            üîÑ Reset Filters
          </button>
        </div>
      </div>

      {expenses.length === 0 ? (
        <div className="no-expenses">
          <p>No expenses found matching the current filters.</p>
          <Link to="/add-expense" className="add-expense-btn">Add Expense</Link>
        </div>
      ) : (
        <>
          <table className="expense-table">
            <thead>
              <tr>
                <th>Description</th>
                <th>Amount</th>
                <th>Category</th>
                <th>Subcategory</th>
                <th>Payment Method</th>
                <th>Date</th>
                <th>Tags</th>
              </tr>
            </thead>
            <tbody>
              {expenses.map(expense => (
                <tr key={expense._id}>
                  <td>{expense.description}</td>
                  <td>{formatIndianRupee(expense.amount)}</td>
                  <td>{expense.category}</td>
                  <td>{expense.subcategory || 'N/A'}</td>
                  <td>{expense.paymentMethod}</td>
                  <td>{new Date(expense.date).toLocaleDateString()}</td>
                  <td>{expense.tags ? expense.tags.join(', ') : 'N/A'}</td>
                </tr>
              ))}
            </tbody>
          </table>

          {monthlyReport && (
            <div className="monthly-report">
              <h3>üìÖ Monthly Report</h3>
              <div className="chart-container">
                <Pie data={preparePieChartData()} />
              </div>

              {renderAIFinancialReport()}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ExpenseList;
