import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Add a request interceptor to add the auth token
api.interceptors.request.use(
  async (config) => {
    try {
      // Get the current user from Firebase Auth
      const user = window.firebase.auth().currentUser;
      if (user) {
        const token = await user.getIdToken();
        config.headers.Authorization = `Bearer ${token}`;
      }
    } catch (error) {
      console.error('Error getting auth token:', error);
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Add a response interceptor to handle errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response) {
      // Handle specific error cases
      switch (error.response.status) {
        case 401:
          // Unauthorized - redirect to login
          window.location.href = '/login';
          break;
        case 403:
          // Forbidden
          console.error('Access forbidden');
          break;
        case 404:
          // Not found
          console.error('Resource not found');
          break;
        case 500:
          // Server error
          console.error('Server error');
          break;
        default:
          console.error('API Error:', error.response.data);
      }
    }
    return Promise.reject(error);
  }
);

export const expenseApi = {
  // Expense-related endpoints
  getExpenses: (params) => api.get('/api/expenses', { params }),
  createExpense: (data) => api.post('/api/expenses', data),
  updateExpense: (id, data) => api.put(`/api/expenses/${id}`, data),
  deleteExpense: (id) => api.delete(`/api/expenses/${id}`),

  // Dashboard-related endpoints
  getDashboardData: () => api.get('/api/dashboard'),
  getMonthlyReport: (params) => api.get('/api/monthly-report', { params }),

  // Health check
  checkHealth: () => api.get('/api/health')
};

export default api;
