import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ExpenseForm = () => {
  const navigate = useNavigate();
  const [expense, setExpense] = useState({
    description: '',
    amount: '',
    category: '',
    subcategory: '',
    paymentMethod: '',
    tags: []
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [aiInsights, setAiInsights] = useState(null);

  // Predefined lists for dropdowns
  const categories = [
    'Food', 'Transportation', 'Entertainment', 
    'Utilities', 'Housing', 'Healthcare', 
    'Education', 'Personal', 'Shopping', 
    'Miscellaneous'
  ];

  const subcategories = {
    'Food': ['Groceries', 'Restaurants', 'Cafes', 'Takeout'],
    'Transportation': ['Public Transit', 'Fuel', 'Taxi/Ride-share', 'Maintenance'],
    'Housing': ['Rent', 'Mortgage', 'Repairs', 'Maintenance'],
    'Utilities': ['Electricity', 'Water', 'Internet', 'Phone'],
    'Healthcare': ['Medicines', 'Doctor Visits', 'Insurance', 'Wellness'],
    'Entertainment': ['Movies', 'Concerts', 'Streaming', 'Hobbies'],
    'Shopping': ['Clothing', 'Electronics', 'Gifts', 'Personal Care'],
    'Miscellaneous': ['Unexpected', 'Donations', 'Education', 'Other']
  };

  const paymentMethods = [
    'Cash', 'Debit Card', 'Credit Card', 
    'Bank Transfer', 'Digital Wallet', 'Other'
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExpense(prevExpense => ({
      ...prevExpense,
      [name]: value
    }));
  };

  const handleTagChange = (e) => {
    const tags = e.target.value.split(',').map(tag => tag.trim()).filter(tag => tag);
    setExpense(prevExpense => ({
      ...prevExpense,
      tags
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setAiInsights(null);

    // Validate input
    if (!expense.description || !expense.amount || !expense.category || !expense.paymentMethod) {
      setError('Please fill in all required fields');
      return;
    }

    const payload = {
      description: expense.description,
      amount: parseFloat(expense.amount),
      category: expense.category,
      subcategory: expense.subcategory || 'General',
      tags: expense.tags,
      paymentMethod: expense.paymentMethod
    };

    console.log('Expense Payload:', payload);

    try {
      const response = await axios.post('http://localhost:5000/api/create', payload, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      console.log('Expense Creation Response:', response.data);

      // Extract AI insights from the response
      if (response.data.aiInsights) {
        setAiInsights(response.data.aiInsights);
      }

      // Reset form and show success message
      setExpense({
        description: '',
        amount: '',
        category: '',
        subcategory: '',
        tags: [],
        paymentMethod: ''
      });

      // Navigate to expense list
      navigate('/');
    } catch (error) {
      console.error('Error adding expense:', error);
      console.error('Error Response:', error.response?.data);
      setError(error.response?.data?.message || 'Failed to add expense. Please try again.');
    }
  };

  return (
    <div className="expense-form-container">
      <h2>âž• Add New Expense</h2>

      {error && <div className="error-message">{error}</div>}

      {success && (
        <div className="success-message">
          <p>âœ… Expense added successfully!</p>
          {aiInsights && (
            <div className="ai-insights">
              <h3>ðŸ’¡ AI Financial Insights</h3>
              <p>{aiInsights}</p>
            </div>
          )}
        </div>
      )}

      <form onSubmit={handleSubmit} className="expense-form">
        <div className="form-group">
          <label>Description *</label>
          <input
            type="text"
            name="description"
            value={expense.description}
            onChange={handleChange}
            placeholder="Enter expense description"
            required
          />
        </div>

        <div className="form-group">
          <label>Amount (â‚¹) *</label>
          <input
            type="number"
            name="amount"
            value={expense.amount}
            onChange={handleChange}
            placeholder="Enter expense amount"
            min="0"
            step="0.01"
            required
          />
        </div>

        <div className="form-group">
          <label>Category *</label>
          <select
            name="category"
            value={expense.category}
            onChange={handleChange}
            required
          >
            <option value="">Select Category</option>
            {categories.map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>

        {expense.category && subcategories[expense.category] && (
          <div className="form-group">
            <label>Subcategory</label>
            <select
              name="subcategory"
              value={expense.subcategory}
              onChange={handleChange}
            >
              <option value="">Select Subcategory</option>
              {subcategories[expense.category].map(subcategory => (
                <option key={subcategory} value={subcategory}>{subcategory}</option>
              ))}
            </select>
          </div>
        )}

        <div className="form-group">
          <label>Payment Method *</label>
          <select
            name="paymentMethod"
            value={expense.paymentMethod}
            onChange={handleChange}
            required
          >
            <option value="">Select Payment Method</option>
            {paymentMethods.map(method => (
              <option key={method} value={method}>{method}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Tags (comma-separated)</label>
          <input
            type="text"
            name="tags"
            value={expense.tags.join(', ')}
            onChange={handleTagChange}
            placeholder="Enter tags (e.g., personal, urgent)"
          />
        </div>

        <button type="submit" className="submit-btn">
          ðŸ’¾ Save Expense
        </button>
      </form>

      {aiInsights && (
        <div className="ai-financial-insights">
          <h3>ðŸ’¡ AI Financial Insights</h3>
          <p>{aiInsights}</p>
        </div>
      )}
    </div>
  );
};

export default ExpenseForm;
