import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import { useNavigate } from 'react-router-dom';
import { formatCurrency } from '../utils/formatCurrency';
import { useAuth } from '../contexts/AuthContext';
import FinancialGuide from './FinancialGuide';
import './ExpenseList.css';

ChartJS.register(ArcElement, Tooltip, Legend);

const ExpenseList = () => {
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const [expenses, setExpenses] = useState([]);
  const [monthlyReport, setMonthlyReport] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    category: '',
    subcategory: '',
    paymentMethod: '',
    minAmount: '',
    maxAmount: ''
  });
  const [appliedFilters, setAppliedFilters] = useState({});

  const categories = [
    'Food', 'Transportation', 'Entertainment', 
    'Utilities', 'Housing', 'Healthcare', 
    'Education', 'Personal', 'Shopping', 
    'Miscellaneous'
  ];

  const paymentMethods = [
    'Cash', 'Credit Card', 'Debit Card', 
    'Bank Transfer', 'Other'
  ];

  const subcategories = {
    'Food': ['Groceries', 'Restaurants', 'Cafes', 'Takeout'],
    'Transportation': ['Public Transit', 'Fuel', 'Taxi/Ride-share', 'Maintenance'],
    'Housing': ['Rent', 'Mortgage', 'Repairs', 'Maintenance'],
    'Utilities': ['Electricity', 'Water', 'Internet', 'Phone'],
    'Healthcare': ['Medicines', 'Doctor Visits', 'Insurance', 'Wellness'],
    'Entertainment': ['Movies', 'Concerts', 'Streaming', 'Hobbies'],
    'Shopping': ['Clothing', 'Electronics', 'Gifts', 'Personal Care'],
    'Miscellaneous': ['Unexpected', 'Donations', 'Education', 'Other']
  };

  // Format Indian Rupee
  const formatRupee = (amount) => {
    return formatCurrency(amount, 'INR');
  };

  const fetchExpenses = useCallback(async () => {
    if (!currentUser) {
      setError('Please log in to view expenses');
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const token = await currentUser.getIdToken();
      const response = await axios.get('http://localhost:5000/api/expenses', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        params: {
          ...appliedFilters,
          page: 1,
          limit: 100
        }
      });

      console.log('Expenses response:', response.data);
      const expensesData = response.data.data || response.data;
      setExpenses(expensesData);
    } catch (err) {
      console.error('Fetch expenses error:', err);
      if (err.response?.status === 401) {
        setError('Please log in again to view expenses');
      } else {
        setError('Failed to fetch expenses. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  }, [currentUser, appliedFilters]);

  const fetchMonthlyReport = useCallback(async () => {
    if (!currentUser) return;

    try {
      const token = await currentUser.getIdToken();
      const response = await axios.get('http://localhost:5000/api/monthly-report', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        params: appliedFilters
      });

      setMonthlyReport(response.data);
    } catch (error) {
      console.error('Error fetching monthly report:', error);
    }
  }, [currentUser, appliedFilters]);

  // Prepare Pie Chart Data with useMemo for performance
  const preparePieChartData = useMemo(() => {
    console.log('Preparing Pie Chart Data. Expenses:', expenses);

    if (expenses.length === 0) {
      console.log('No expenses to create pie chart');
      return null;
    }

    // Calculate category totals
    const categoryTotals = expenses.reduce((acc, expense) => {
      acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
      return acc;
    }, {});

    console.log('Category Totals:', categoryTotals);

    // Prepare data for pie chart
    const chartData = {
      labels: Object.keys(categoryTotals),
      datasets: [{
        data: Object.values(categoryTotals),
        backgroundColor: [
          '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', 
          '#9966FF', '#FF9F40', '#FF6384', '#C9CBCF'
        ],
        hoverBackgroundColor: [
          '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', 
          '#9966FF', '#FF9F40', '#FF6384', '#C9CBCF'
        ]
      }]
    };

    console.log('Pie Chart Data:', chartData);
    return chartData;
  }, [expenses]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const applyFilters = () => {
    // Create a new object with only non-empty filter values
    const newAppliedFilters = Object.fromEntries(
      Object.entries(filters)
        .filter(([_, value]) => value !== '')
        .map(([key, value]) => {
          // Ensure numeric filters are parsed correctly
          if (key === 'minAmount' || key === 'maxAmount') {
            return [key, parseFloat(value).toFixed(2)];
          }
          return [key, value];
        })
    );

    // Update applied filters to trigger useEffect
    setAppliedFilters(newAppliedFilters);
  };

  const resetFilters = () => {
    // Reset both filters and applied filters
    setFilters({
      category: '',
      subcategory: '',
      paymentMethod: '',
      minAmount: '',
      maxAmount: ''
    });
    setAppliedFilters({});
  };

  // Delete expense with immediate UI update
  const handleDeleteExpense = async (expenseId) => {
    // Log the expense ID for debugging
    console.log('Attempting to delete expense with ID:', expenseId);

    // Validate the expense ID
    if (!expenseId) {
      console.error('Invalid expense ID: undefined or null');
      alert('Error: Unable to delete expense. Invalid ID.');
      return;
    }

    const confirmDelete = window.confirm('Are you sure you want to delete this expense?');

    if (confirmDelete) {
      try {
        const token = await currentUser.getIdToken();
        const response = await axios.delete(`http://localhost:5000/api/delete/${expenseId}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (response.data.success) {
          // Immediately refetch expenses to ensure latest data
          await fetchExpenses();

          // Optional: Update monthly report
          await fetchMonthlyReport();

          // Show success message
          alert('Expense deleted successfully');
        }
      } catch (error) {
        console.error('Delete expense error:', error);
        alert('Failed to delete expense');
      }
    }
  };

  const renderAIFinancialReport = () => {
    if (!monthlyReport) return null;

    return (
      <div className="ai-insights-section">
        <div className="ai-insights-header">
          <h3>AI Financial Insights</h3>
        </div>

        <div className="ai-insights-card">
          <div className="ai-insights-content">
            {monthlyReport.insights.map((insight, index) => (
              <div 
                key={index} 
                className="ai-tip"
                data-type={insight.type}
                style={{animationDelay: `${index * 0.1}s`}}
              >
                <div className="ai-tip-icon">
                  {getInsightIcon(insight.type)}
                </div>
                <div className="ai-tip-content">
                  <div className="ai-tip-title">
                    {insight.title}
                    {insight.type === 'achievement' && 'üéâ'}
                    {insight.type === 'warning' && '‚ö†Ô∏è'}
                  </div>
                  <div className="ai-tip-description">
                    {insight.description}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {monthlyReport.summary && (
          <div className="ai-insights-card">
            <div className="ai-tip">
              <div className="ai-tip-icon">üìä</div>
              <div className="ai-tip-content">
                <div className="ai-tip-title">Monthly Summary</div>
                <div className="ai-tip-description">
                  {monthlyReport.summary}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };

  const getInsightIcon = (type) => {
    const icons = {
      saving: 'üí∞',
      warning: '‚ö†Ô∏è',
      trend: 'üìà',
      suggestion: 'üí°',
      achievement: 'üéØ',
      budget: 'üìä',
      spending: 'üí≥',
      income: 'üíµ',
      default: 'üìã'
    };
    return icons[type] || icons.default;
  };

  useEffect(() => {
    fetchExpenses();
    fetchMonthlyReport();
  }, [fetchExpenses, fetchMonthlyReport]);

  if (loading) {
    return <div className="loading">Loading expenses...</div>;
  }

  if (error) {
    return (
      <div className="error-message">
        <h2>Error</h2>
        <p>{error}</p>
        <p>Please ensure the backend server is running on http://localhost:5000</p>
      </div>
    );
  }

  return (
    <div className="expense-list-container">
      <div className="expenses-header">
        <h2>üí∏ Expense List</h2>
        <button 
          className="add-expense-btn" 
          onClick={() => navigate('/add-expense')}
        >
          ‚ûï Add New Expense
        </button>
      </div>

      <div className="expense-filters-container">
        <div className="filter-container">
          <div className="filter-inputs">
            <div className="filter-group">
              <label>Category</label>
              <select
                name="category" 
                value={filters.category} 
                onChange={handleFilterChange}
              >
                <option value="">All Categories</option>
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
            </div>

            {filters.category && subcategories[filters.category] && (
              <div className="filter-group">
                <label>Subcategory</label>
                <select 
                  name="subcategory" 
                  value={filters.subcategory} 
                  onChange={handleFilterChange}
                >
                  <option value="">All Subcategories</option>
                  {subcategories[filters.category].map(subcategory => (
                    <option key={subcategory} value={subcategory}>{subcategory}</option>
                  ))}
                </select>
              </div>
            )}

            <div className="filter-group">
              <label>Payment Method</label>
              <select 
                name="paymentMethod" 
                value={filters.paymentMethod} 
                onChange={handleFilterChange}
              >
                <option value="">All Payment Methods</option>
                {paymentMethods.map(method => (
                  <option key={method} value={method}>{method}</option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label>Min Amount (‚Çπ)</label>
              <input 
                type="number" 
                name="minAmount" 
                value={filters.minAmount} 
                onChange={handleFilterChange} 
                placeholder="Minimum Amount"
                min="0"
              />
            </div>

            <div className="filter-group">
              <label>Max Amount (‚Çπ)</label>
              <input 
                type="number" 
                name="maxAmount" 
                value={filters.maxAmount} 
                onChange={handleFilterChange} 
                placeholder="Maximum Amount"
                min="0"
              />
            </div>
          </div>

          <div className="filter-actions">
            <button 
              onClick={applyFilters} 
              className="btn btn-secondary"
            >
              üîç Apply Filters
            </button>
            <button 
              onClick={resetFilters} 
              className="btn btn-outline-primary"
            >
              üîÑ Reset
            </button>
          </div>
        </div>
      </div>

      <div className="expense-table-container">
        {expenses.length === 0 ? (
          <div className="no-expenses">
            <p>No expenses found matching the current filters.</p>
            <button 
              className="add-expense-btn"
              onClick={() => navigate('/add-expense')}
            >
              ‚ûï Add Expense
            </button>
          </div>
        ) : (
          <div className="expense-table-scrollable">
            <table className="expense-table">
              <thead>
                <tr>
                  <th>Description</th>
                  <th>Amount</th>
                  <th>Category</th>
                  <th>Subcategory</th>
                  <th>Payment Method</th>
                  <th>Date</th>
                  <th>Tags</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {expenses.map((expense) => (
                  <tr key={expense._id || expense.id}>
                    <td>{expense.description}</td>
                    <td>{formatRupee(expense.amount)}</td>
                    <td>{expense.category}</td>
                    <td>{expense.subcategory || 'N/A'}</td>
                    <td>{expense.paymentMethod}</td>
                    <td>{new Date(expense.date).toLocaleDateString()}</td>
                    <td>{expense.tags ? expense.tags.join(', ') : 'N/A'}</td>
                    <td>
                      <button 
                        onClick={() => handleDeleteExpense(expense._id || expense.id)}
                        className="btn btn-danger btn-sm"
                      >
                        üóëÔ∏è Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      <div className="monthly-report">
        {/* Pie Chart */}
        <div className="chart-container">
          {preparePieChartData && (
            <Pie 
              data={preparePieChartData} 
              options={{ 
                responsive: true, 
                maintainAspectRatio: false,
                layout: {
                  padding: 10
                },
                plugins: {
                  title: {
                    display: true,
                    text: 'Expense Distribution',
                    font: {
                      size: 18,
                      weight: 'bold'
                    }
                  },
                  legend: {
                    position: 'bottom',
                    labels: {
                      boxWidth: 20,
                      font: {
                        size: 12
                      }
                    }
                  },
                  tooltip: {
                    backgroundColor: '#333',
                    titleColor: '#fff',
                    bodyColor: '#fff',
                    borderColor: '#666',
                    borderWidth: 1
                  }
                }
              }} 
            />
          )}
          {!preparePieChartData && (
            <div>No expense data available for chart</div>
          )}
        </div>

        {/* Financial Guide */}
        {!loading && expenses.length > 0 && (
          <FinancialGuide 
            expenses={expenses} 
            monthlyReport={monthlyReport}
          />
        )}

        {/* AI Financial Report */}
        <div className="ai-financial-report">
          {monthlyReport && (
            <div>
              <h3>Financial Insights</h3>
              <p>{monthlyReport.healthAssessment}</p>

              <h4>Top Categories</h4>
              <ul>
                {monthlyReport.topCategories.map((category, index) => (
                  <li key={index}>
                    {category.name}: {formatRupee(category.total)} 
                    ({category.percentage.toFixed(2)}%)
                  </li>
                ))}
              </ul>

              <h4>Recommendations</h4>
              <ul>
                {monthlyReport.recommendations.map((rec, index) => (
                  <li key={index}>{rec}</li>
                ))}
              </ul>
            </div>
          )}
        </div>

        {/* AI Financial Tips */}
        {renderAIFinancialReport()}
      </div>
    </div>
  );
};

export default ExpenseList;
